import base64,struct

bmp_data = base64.b64decode('')

def bmp_info(data):
    bi = struct.unpack('<ccIIIIIIHH', data[:30]) #必须切片30位
    if (bi[0] == b'B') and (bi[1] == b'M'):
        width = bi[6]
        height = bi[7]
        color = bi[9]
        return {
           'width': width,
            'height': height,
            'color': color
        }
    else:
        return None

# 测试
bi = bmp_info(bmp_data)
print(bi)

